pragma solidity ^0.5.0;

/**
 * @title Currency Oracle
 * @dev Stores currencies rates from outside
 */
contract CurrencyRatesContract {

    address payable public owner;
    uint public currenciesCount = 0;

    mapping (uint => uint) public rates;
    mapping (uint => Currency) public currencies;

    struct Currency {
        uint64 networkId;
        string name;
        string symbol;
    }

	event RateChanged  ( uint currencyId, uint rate );

	/**
	 * constructor
	 */
    constructor() public {
        owner = msg.sender;

		// default currencies
        currencies[currenciesCount] = Currency(22177, 'Charg Coin', 'CHG');
        rates[currenciesCount] = 1 * 10**18;
        currenciesCount++;
		currencies[currenciesCount++] = Currency(1, 'Ethereum', 'ETH');
		currencies[currenciesCount++] = Currency(56, 'Binance Coin', 'BNB');
		currencies[currenciesCount++] = Currency(137, 'Matic Token', 'MATIC');
		currencies[currenciesCount++] = Currency(32659, 'FSN Token', 'FSN');

		currencies[currenciesCount++] = Currency(0, 'Bitcoin', 'BTC');
		currencies[currenciesCount++] = Currency(0, 'Litecoin', 'LTC');
		currencies[currenciesCount++] = Currency(0, 'Dogecoin', 'DOGE');
    }

	function() external payable {
		revert();
	}

	function addCurrency( uint64 networkId, string memory name, string memory symbol ) public {
        require(msg.sender == owner, "only owner");
		currencies[currenciesCount++] = Currency(networkId, name, symbol);
	}

	function setRate( uint currencyId, uint rate ) public {
        require(msg.sender == owner, "only owner");
		rates[currencyId] = rate;
        emit RateChanged (currencyId, rate);
	}
}
